package com.usu.test.leetcode;

import java.util.HashMap;

public class test657_judge_route_circle extends Thread {
	public void run() {
		String test = "";
		String test2 = "UD";
		System.out.println(judgeCircle(test2));
	}
	
	public static void main(String args[]) {
		new test657_judge_route_circle().start();
	}
	
	public boolean judgeCircle(String moves) {
        HashMap<Character, Integer> counts = new HashMap<>();
        
        Character c;
        
        for (int i = 0; i < moves.length(); i++) {
            c = new Character(moves.charAt(i));
            int cnt = 0;
            if (counts.get(c) != null) {
                cnt = counts.get(c);
            }
            counts.put(c, cnt + 1);
        }
        
        int lCount = counts.get('L') != null ? counts.get('L') : 0;
        int rCount = counts.get('R') != null ? counts.get('R') : 0;
        int uCount = counts.get('U') != null ? counts.get('U') : 0;
        int dCount = counts.get('D') != null ? counts.get('D') : 0;
        
        if (lCount == rCount && uCount == dCount) return true;

        return false;
    }
}
